<?phpdeclare(strict_types=1);namespace CityBuild\task;use CityBuild\CityBuild;use pocketmine\scheduler\Task;class Tip extends Task{    private $main;    public function __construct(CityBuild $main){        $this->main = $main;    }    public function onRun(int $currentTick){    	$tips = $this->main->settings->get("tip");        $tips = $tips[array_rand($tips)];        $tip = "$tips";        $tip = str_replace("&", "ยง", $tip);        $this->main->getServer()->broadcastTip($tip);    }}