<?phpnamespace CityBuild\task;use CityBuild\CityBuild;use pocketmine\scheduler\Task;class Tip5 extends Task{    private $main;    public function __construct(CityBuild $main){        $this->main = $main;    }    public function onRun(int $currentTick){    	$ws = $this->main->settings->get("tip5");        $ws = $ws[array_rand($ws)];        $w = "$ws";        $w = str_replace("&", "ยง", $w);        $this->main->getServer()->broadcastTip($w);    }}